    total_receivables: Optional[Union[StrictFloat, StrictInt]] = Field(default=None, description="<p>{{TotalReceivables}} is the sum of all receivables owed by customers and affiliates within one year, including {{Accts_Rec}}, {{NotesReceivable}}, {{LoansReceivable}},{{OtherCurrentReceivables}}.</p>")
